value.Reminder stage:
={{ (() => {
  // 1. Get the current stage and clean it
  const currentStage = ($('Should email be sent').item.json['Reminder stage'] || "").toString().trim();

  // 2. Define the "Next Stage" map
  const nextStageMap = {
    "": "Message 1",
    "Message 1": "Message 2",
    "Message 2": "Message 3",
    "Message 3": "Message 4"
  };

  // 3. Return the next stage, or keep current if it's already Message 4 or unknown
  return nextStageMap[currentStage] || currentStage;
})() }}

