={{ (() => {
    // How many days to wait?
    let waitDays = 3;
    // Prepare the Stage Variable (Clean it up)
    let stage = ($json['Reminder stage'] || '').toString().trim();
    // Define the empty state for stage
    let isNewContact = (stage === 'Message 1');
    // Date Logic: Only consider the calendar day (ignore time and timezone)
    // Parse the last message date and strip it down to just the calendar date
    let lastMsg = DateTime.fromFormat($json['Last message'], 'dd LLL yyyy, H:mm', { zone: 'utc' }).startOf('day');
    let now = DateTime.utc().startOf('day');

    // Check if the target date (last message + wait days) is today or in the past.
    let enoughTimePassed = lastMsg.plus({ days: waitDays }) <= now;
    // Change 'isNewContact' to any variable above to target a different stage
    return enoughTimePassed && isNewContact;
})() }}

