// Get current date info
const now = new Date();
const currentDay = now.getDay(); // 0 = Sunday, 1 = Monday, etc.
const currentYear = now.getFullYear();
const currentMonth = now.getMonth();
const currentDate = now.getDate();
const today = new Date(currentYear, currentMonth, currentDate);

// Map day names to day numbers
const dayToNumber = {
  "Sunday": 0,
  "Monday": 1,
  "Tuesday": 2,
  "Wednesday": 3,
  "Thursday": 4,
  "Friday": 5,
  "Saturday": 6
};

// Process each contact
const toSendToday = [];
const items = $input.all();

items.forEach(item => {
  // Extract relevant data
  const phone = item.json.Phone;
  const frequency = item.json['Reminder freq.'];
  const preferredDay = item.json['Reminder day'];
  const reminderHourFull = item.json['Reminder hour']; // This might contain "11:00" format
  const period = item.json['Reminder period'];
  const lastSentStr = item.json['Last sent'];

  // Skip if missing required data
  if (!phone || !frequency || !preferredDay) return;

  // Extract the hour value from the time string (e.g., "11:00" -> 11)
  let preferredHour = parseInt(reminderHourFull);

  // Parse last sent date if available
  let lastSent = null;
  if (lastSentStr && lastSentStr.trim() !== '') {
    lastSent = new Date(lastSentStr);
  }

  // Calculate hour in 24-hour format
  let hour24 = preferredHour;
  if (period && period.toUpperCase() === 'P.M.' && preferredHour < 12) {
    hour24 += 12;
  }
  if (period && period.toUpperCase() === 'A.M.' && preferredHour === 12) {
    hour24 = 0;
  }

  // Check if today matches the preferred day
  const preferredDayNumber = dayToNumber[preferredDay];
  if (currentDay === preferredDayNumber) {
    let shouldSend = false;

    // Handle different frequency formats
    const frequencyLower = frequency.toLowerCase();

    // For daily/daily reminders
    if (frequencyLower === '1 day' || frequencyLower === 'daily') {
      shouldSend = true;
    } 
    // For weekly/7 days reminders
    else if (frequencyLower === '7 days' || frequencyLower === 'weekly') {
      if (!lastSent || dateDiffInDays(lastSent, today) >= 7) {
        shouldSend = true;
      }
    }
    // For bi-weekly/14 days reminders
    else if (frequencyLower === '14 days' || frequencyLower === 'bi-weekly') {
      if (!lastSent || dateDiffInDays(lastSent, today) >= 14) {
        shouldSend = true;
      }
    }
    // For monthly/30 days reminders
    else if (frequencyLower === '30 days' || frequencyLower === 'monthly') {
      if (!lastSent || dateDiffInDays(lastSent, today) >= 30) {
        shouldSend = true;
      }
    }

    if (shouldSend) {
      toSendToday.push({
        phone,
        hour: hour24,
        frequency,
        preferredDay,
        angelName: item.json['Angel name'],
        lastSent: lastSent ? lastSent.toISOString() : null,
        originalItem: item.json
      });
    }
  }
});

// Helper function to calculate difference in days between two dates
function dateDiffInDays(date1, date2) {
  const _MS_PER_DAY = 1000 * 60 * 60 * 24;
  // Discard the time and time-zone information
  const utc1 = Date.UTC(date1.getFullYear(), date1.getMonth(), date1.getDate());
  const utc2 = Date.UTC(date2.getFullYear(), date2.getMonth(), date2.getDate());

  return Math.floor((utc2 - utc1) / _MS_PER_DAY);
}

// Return the filtered list of reminders to send today
return toSendToday;

