={{ (() => {
  // Parse the date from $json['Last sent'] (format: "16-Apr-2025")
  const parts = $json['Last sent'].split('-');
  const day = parseInt(parts[0]);
  const months = {"Jan":0, "Feb":1, "Mar":2, "Apr":3, "May":4, "Jun":5, "Jul":6, "Aug":7, "Sep":8, "Oct":9, "Nov":10, "Dec":11};
  const month = months[parts[1]];
  const year = parseInt(parts[2]);

  // Create a date object from $json['Last sent']
  const lastSentDate = new Date(year, month, day);

  // Add 24 hours (86400000 milliseconds)
  const lastSentPlus24h = new Date(lastSentDate.getTime() + 7*86400000);

  // Get current date from new Date().toISOString()
  const currentDate = new Date();

  // Compare if current date is greater than or equal to lastSentDate + 24h
  return currentDate >= lastSentPlus24h;
})() }}

