// Get current date and time
const now = new Date();
// Calculate expiry time (current time + expires_in seconds)
const expiryTime = new Date(now.getTime() + $input.first().json.expires_in * 1000);

// Format the dates
const formattedNow = now.toISOString();
const formattedExpiry = expiryTime.toISOString();

// Prepare data for spreadsheet update
const updateData = [
  {
    "Variable Name": "canvaAccessToken",
    "Variable Value": $input.first().json.access_token,
    "Last Updated": formattedNow
  },
  {
    "Variable Name": "canvaRefreshToken",
    "Variable Value": $input.first().json.refresh_token,
    "Last Updated": formattedNow
  },
  {
    "Variable Name": "canva_token_expiry",
    "Variable Value": formattedExpiry,
    "Last Updated": formattedNow
  }
];

return {json: {data: updateData}};

